import React, { useState } from 'react'
import Carte from './tables/UserTable'//fichier qui affiche les listes qui vont ajouter 
import Commentaireajout from './commentaire'
import AddUserForm from './ajouter'//fichier qui ajoute le formulaire d'ajout de produit
import './App.css';

const App = () => {
  const usersData = [

  ]

  const [users, setUsers] = useState(usersData)

  const addUser = user => {
		user.id = users.length
		setUsers([ ...users, user ])
  }

  const Data = []

  const [utilisateur, setutilisateur] = useState(Data)

  const commentajout = user1 => {
		user1.id = utilisateur.length
		setutilisateur([ ...utilisateur, user1 ])
  }

  return (
    <div className="container">
      <div className="flex-row">
        <div className="flex-large">
          <AddUserForm addUser={addUser} />
        </div><br></br>
        <div className="flex-large">
          <Carte users={users} />
          <Commentaireajout commentajout={commentajout}/>
        </div>
      </div>
    </div>
  )
}

export default App
