import React, { useState } from 'react'
import UserTable from './tables/UserTable'
import './App.css';

const App = () => {
  const usersData = [

  ]

  const [users, setUsers] = useState(usersData)

  const addUser = user => {
		user.id = users.length
		setUsers([ ...users, user ])
	}

	const deleteUser = id => {
		setUsers(users.filter(user => user.id !== id))
	}

  return (
    <div className="container">
      <div className="flex-row">
        <div className="flex-large">
          <AddUserForm addUser={addUser} />
        </div><br></br>
        <div className="flex-large">
          <UserTable users={users} deleteUser={deleteUser} />
        </div>
      </div>
    </div>
  )
}

const AddUserForm = props => {
	const initialFormState = { id: null, produit: '', prix: '', description: '' }
	const [ user, setUser ] = useState(initialFormState)

	const handleInputChange = event => {
		const { name, value } = event.target

		setUser({ ...user, [name]: value })
	}

	return (
		<form
			onSubmit={event => {
				event.preventDefault()
				if (!user.produit || !user.prix || user.description) return

        if(isNaN(user.prix)){
					var erreur = document.getElementById('erreur')
					erreur.innerHTML = 'Entrer un nombre'
			}else{
					var yes = document.getElementById('erreur')
					yes.innerHTML = "";
					props.addUser(user)
					props.setCount(props.count +1)
					setUser(initialFormState)		
			}
			}}
		>		<center>


          <label id="nom">Produit &nbsp;&nbsp;</label>
          <input type="text" name="produit" value={user.produit} onChange={handleInputChange} />
        <br/>

            <label id="nom">&nbsp;&nbsp;Prix &nbsp;&nbsp;</label>
            <input id="inputprix" type="text" name="prix" value={user.prix} onChange={handleInputChange} /> 
            &nbsp;&nbsp;&nbsp;<br/>
            <p id="erreur"></p>
        <br/>

        <label id="nom">Description &nbsp;&nbsp;</label>
          <input type="text" name="description" value={user.description} onChange={handleInputChange} />

         <button className="btn btn-primary">Ajouter</button>
    </center>
		</form>

	)
}	

export default App
