import React, { Component } from 'react';
import Img from 'react-image'

class Coeur extends Component {

    constructor(props) {
        super(props);
        this.state = {
          clicks: 0,
        };
    }
    IncrementItem(){
        this.setState({ clicks: this.state.clicks + 1 });
    }
    render(){
        return  (
            <div>
                <span onClick={()=> this.IncrementItem()}>{this.state.clicks}<Img id="icone" src="images/coeur.jpg"/></span>
            </div>
        )
    }

}
export default Coeur;
