import React, { useState } from 'react'
import './App.css';


const Commentaire = () => {

	const Data = []

	const [use, setUse] = useState(Data)

  const commentajout = user => {
		user.id = use.length + 1
		setUse([ ...use, user ])
  }


	const initialFormState = { id: null, commentaire: ''}
	const [ user, setUsers ] = useState(initialFormState)

	const handleInputChange = event => {
		const { name, value } = event.target

		setUsers({ ...user, [name]: value })
	}

	return (
		<form
			onSubmit={event => {
				event.preventDefault()
                if (!user.commentaire ) return
					commentajout(user)
					setUsers(initialFormState)	
			}}
		>	<center className="tableau">

          <table>
            <tbody>
              <tr>
                <td><input type="text" name="commentaire" value={user.commentaire} onChange={handleInputChange} />
                &nbsp;<button className="btn btn-primary btn-lg">Comment</button>
                </td>
              </tr><br/>
            </tbody>
            </table>
            <br/><br/>
              <table>
                {use.length > 0 ? (
                    use.map(user => (
                      <tbody>
                    <tr key={user.id}>
                    <td id="tdcomment">{user.commentaire}</td>
                  </tr><hr id="ligne"/>
                    </tbody>  
              ))
            ) : (
              <tr>
                <td>aucun commentaire</td>
              </tr>
            )}
                </table>
      </center>

		</form>
	)
}
export default Commentaire;
