import React, { useState } from 'react'
import Carte from './tables/UserTable'//fichier qui affiche les listes qui vont ajouter 
import Commentaire from './commentaire'
import Commentaireajout from './commentaire'
import AddUserForm from './ajouter'//fichier qui ajoute le formulaire d'ajout de produit
import './App.css';

const App = () => {
  const usersData = [

  ]

  const [users, setUsers] = useState(usersData)

  const Data = []

  const [userss, setUserss] = useState(Data)

  const addUser = user => {
		user.id = userss.length
		setUsers([ ...users, user ])
  }


  const commentajout = user => {
		user.id = users.length
		setUsers([ ...users, user ])
  }

  return (
    <div className="container">
      <div className="flex-row">
        <div className="flex-large">
          <AddUserForm addUser={addUser} />
        </div><br></br>
        <div className="flex-large">
          <Carte users={users} />
        </div>
        <Commentaire commentajout={commentajout}/>

        <Commentaireajout userss={userss}/>
      </div>
    </div>
  )
}

export default App
